import React, { useState, useEffect } from "react";
import TutorialDataService from "../services/TutorialService";

const Tutorial = props => {
  const initialTutorialState = {
    name: "",
    address: "",
    city: "",
    province: "",
    registrationDate: "",
    status: ""
  };
  const [currentDataCust, setCurrentTutorial] = useState(initialTutorialState);
  const [message, setMessage] = useState("");

  const getTutorial = id => {
    TutorialDataService.get(id)
      .then(response => {
        setCurrentTutorial(response.data.customerData);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  useEffect(() => {
    getTutorial(props.match.params.id);
  }, [props.match.params.id]);

  const handleInputChange = event => {
    const { name, value } = event.target;
    setCurrentTutorial({ ...currentDataCust, [name]: value });
  };

  const updateTutorial = () => {
    TutorialDataService.update(currentDataCust.id, currentDataCust)
      .then(response => {
        console.log(response.data);
        setMessage("The tutorial was updated successfully!");
      })
      .catch(e => {
        console.log(e);
      });
  };

  return (
    <div>
        <div className="detail-form">
          <h4>Detail Customer</h4>
          <form style={{width:"100%"}}>
            <div className="form-group" >
              <label htmlFor="name">Name</label>
              <input 
                type="text"
                className="form-control"
                id="name"
                name="name"
                value={currentDataCust.name}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input 
                type="text"
                className="form-control"
                id="address"
                name="address"
                value={currentDataCust.address}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="city">City</label>
              <input 
                type="text"
                className="form-control"
                id="city"
                name="city"
                value={currentDataCust.city}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="province">Province</label>
              <input 
                type="text"
                className="form-control"
                id="province"
                name="province"
                value={currentDataCust.province}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="registrationDate">Registration Date</label>
              <input disabled
                type="text"
                className="form-control"
                id="registrationDate"
                name="registrationDate"
                value={currentDataCust.registrationDate}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="status">Status</label>
              <input 
                type="text"
                className="form-control"
                id="status"
                name="status"
                value={currentDataCust.status}
                onChange={handleInputChange}
              />
            </div>  
          </form>

          <button className="btn btn-primary" onClick={updateTutorial} style= {{marginTop: "5px", marginBottom: "50px", marginRight: "20px"}}>
            Update Customer
          </button>
          <p>{message}</p>

          <button className="btn btn-success" style= {{marginTop: "5px", marginBottom: "50px"}}>
            Backt to Homepage
          </button>
        </div>

    </div>
  );
