import React, { useState, useEffect } from "react";
import TutorialDataService from "../services/TutorialService";

const Detail = props => {
  const initialTutorialState = {
    id: null,
    title: "",
    description: "",
    published: false
  };
  const [currentTutorial, setCurrentTutorial] = useState(initialTutorialState);
  const [message, setMessage] = useState("");

  const getTutorial = id => {
    TutorialDataService.get(id)
      .then(response => {
        console.log(response);
        setCurrentTutorial(response.data.customerData);
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  useEffect(() => {
    getTutorial(props.match.params.id);
  }, [props.match.params.id]);

  const handleInputChange = event => {
    const { name, value } = event.target;
    setCurrentTutorial({ ...currentTutorial, [name]: value });
  };

  const updatePublished = status => {
    var data = {
      id: currentTutorial.id,
      title: currentTutorial.title,
      description: currentTutorial.description,
      published: status
    };

    TutorialDataService.update(currentTutorial.id, data)
      .then(response => {
        setCurrentTutorial({ ...currentTutorial, published: status });
        console.log(response.data);
        setMessage("The status was updated successfully!");
      })
      .catch(e => {
        console.log(e);
      });
  };

  const updateTutorial = () => {
    TutorialDataService.update(currentTutorial.id, currentTutorial)
      .then(response => {
        console.log(response.data);
        setMessage("The tutorial was updated successfully!");
      })
      .catch(e => {
        console.log(e);
      });
  };

  const deleteTutorial = () => {
    TutorialDataService.remove(currentTutorial.id)
      .then(response => {
        console.log(response.data);
        props.history.push("/tutorials");
      })
      .catch(e => {
        console.log(e);
      });
  };

  return (
    <div>
      {currentTutorial ? (
        <div className="detail-form">
          <h4>Detail Customer</h4>
          <form style={{width:"100%"}}>
            <div className="form-group" >
              <label htmlFor="name">Name</label>
              <input disabled 
                type="text"
                className="form-control"
                id="name"
                name="name"
                value={currentTutorial.name}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input disabled
                type="text"
                className="form-control"
                id="address"
                name="address"
                value={currentTutorial.address}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="city">City</label>
              <input disabled
                type="text"
                className="form-control"
                id="city"
                name="city"
                value={currentTutorial.city}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="province">Province</label>
              <input disabled
                type="text"
                className="form-control"
                id="province"
                name="province"
                value={currentTutorial.province}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="registrationDate">Registration Date</label>
              <input disabled
                type="text"
                className="form-control"
                id="registrationDate"
                name="registrationDate"
                value={currentTutorial.registrationDate}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="status">Status</label>
              <input disabled
                type="text"
                className="form-control"
                id="status"
                name="status"
                value={currentTutorial.status}
                onChange={handleInputChange}
              />
            </div>  
          </form>

          <button className="btn btn-success" onClick={goToHomePage} style= {{marginTop: "5px"}}>
            Backt to Homepage
          </button>
        </div>
      ) : (
        <div>
          <br />
          <p>Please click on a Tutorial...</p>
        </div>
      )}
    </div>
  );
};

export default Detail;
